class Solution:
    def waysToReachStair(self, k: int) -> int:
        if k==0:
            return 2
        if k==1:
            return 4
        res=0
        cur=1
        while (n:=(2**cur-k))<=cur+1:
            if n>=0:
                res+=comb(cur+1,n)
            cur+=1
        return res
