class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def helper(x, y, x_first=True):
            # x cannot be next each other
            assert x <= y + 1 

            if x == y + 1:
                return 1 if x_first else 0

            if x == 0:
                return 1

            if x_first:
                return helper(x - 1, y, False) + helper(x, y - 1, True)
            return helper(x, y - 1, True)


        ans = 0
        if k == 1 or k == 0:
            ans += 1

        for i in range(50):
            delta = 2 ** (i + 1) - 1 + 1 - k
            if 0 <= delta <= i + 2:

                ans += helper(delta, i + 1)

        return ans
