class Solution {
public:
    int waysToReachStair(int k) {
        long long cur=1,ans=0;
        if(cur==k) ans++;
        if(cur-1==k) ans++;
        for(int i=0;i<32;i++){
            cur=cur+pow(2,i);
            if(cur>=k && cur-k<=i+2){
                long long req=cur-k,temp=1,cur=0;
                long long total=i+2;
                while(req>cur){
                    cur++;
                    temp=(temp*total)/cur;
                    total--;
                }
                ans=ans+temp;
            }
        }
        return ans;
    }
};
