class Solution {
    int fn(int i,int j,int &n,map<int,map<int,int>> &dp){
        if(i>n+1) return 0;
        if(dp.count(j)&&dp[j].count(i)) return dp[j][i];
        return dp[j][i]=(i==n)+(i==n+1)+fn(i-1+(1<<j),j+1,n,dp)+fn(i+(1<<j),j+1,n,dp);
    }
public:
    int waysToReachStair(int k) {
        map<int,map<int,int>> dp;
        return fn(1,0,k,dp);
    }
};
