import math
class Solution:
    def waysToReachStair(self, k: int) -> int:

        answer = int(k == 0) + int(k == 1) # 0 jump

        for jump in range(1, 40):
            if 1 + sum(1 << i for i in range(jump)) - (jump + 1) <= k <= 1 + sum(1 << i for i in range(jump)):
                down = k - (1 + sum(1 << i for i in range(jump)) - (jump + 1))
                answer += math.comb(jump + 1, down)

        return answer
