class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dfs(i, jump, f):
            res = 1 if i == k else 0
            if not f and i > 0: res += dfs(i - 1, jump, True)
            if i <= k:
                res += dfs(i + 2 ** jump, jump + 1, False)
            return res
        return dfs(1, 0, False)
