class Solution {
public:
    long long cc[35][35];

    int waysToReachStair(int k) {

        for (int i = 1; i < 32; ++i) {
            cc[i][0] = cc[i][i] = 1;
            for (int j = 1; j < i; ++j) {
                cc[i][j] = cc[i-1][j-1] + cc[i-1][j]; 
            }
        }

        int ans = 0;
        for (int i = 0; i < 32; ++i) {
            long long tmp = 1;
            for (int j = 0; j < i; ++j) tmp += 1LL << j;
            for (int j = 0; j <= i + 1; ++j) {
                if (tmp - j == k) {
                    ans += cc[i+1][j];
                    // cout << i << ' ' << j << endl;
                    break;
                }
            }
        }
        return ans;
    }
};
