class Solution {
public:
    int target;
    unordered_map<int,unordered_map<int,unordered_map<int,int>>>mp;
    int getcount(int pos,int jump,int last){
        if(pos>target+1) return 0;
        if(mp.find(pos)!=mp.end() && mp[pos].find(jump)!=mp[pos].end() && mp[pos][jump].find(last)!=mp[pos][jump].end()){
            return mp[pos][jump][last];
        }
        int count=0;
        if(pos==target) count++;
        if(pos!=0 && last!=0){
            count+=getcount(pos-1,jump,0);
        }
        count+=getcount(pos+pow(2,jump),jump+1,1);
        return mp[pos][jump][last]=count;
    }
    int waysToReachStair(int k) {
        target=k;
        return getcount(1,0,1);
    }
};
