class Solution:
    def waysToReachStair(self, k: int) -> int:

        dp = {}
        def solve(ind, used, jump):  
            if ind > k + 1: return 0
            if (ind, used, jump) in dp: return dp[ind, used, jump]

            res = int(ind == k)
            res += solve(ind + (1 << jump), 0, jump + 1)

            if used == 0:
                res += solve(ind - 1, 1, jump)

            dp[(ind, used, jump)] = res
            return dp[ind, used, jump]

        res = solve(1, 0, 0)
        print(dp)
        return res
