class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dp(pos, curr_jump, can_down):
            if (can_down and pos - 1 > k) or (not can_down and pos > k):
                return 0

            res = int(pos == k)

            npos = pos + (2 ** curr_jump)
            res += dp(npos, curr_jump + 1, True)

            if can_down:
                res += dp(pos - 1, curr_jump, False)

            return res

        return dp(1, 0, True)
