class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dp(pos, up, down):
            res = (1 if pos == k else 0)
            if not down and 2**up - up - 1 > k:
                return res
            res += dp(pos + (2**up), up+1, False)
            if pos > 0 and not down:
                res += dp(pos - 1, up, True)
            return res
        return dp(1, 0, False)
