class Solution:
    def waysToReachStair(self, k: int) -> int:
        j = 0
        dp = [defaultdict(int) for _ in range(32)]
        dp[0][1] = 1
        dp[0][0] = 1
        j = 0
        for i in range(1, 32):
            for u, v in dp[i-1].items():
                dp[i][u+2**j] += v
                dp[i][u+2**j-1] += v
            j += 1
        ans = 0
        for i in range(32):
            ans += dp[i][k]
        return ans
