public class Solution
{
	// @TrickyCat
	public int WaysToReachStair(int k)
	{
		var memo = new Dictionary<(int i, int jump, bool canGoDown), int>();
		return F(1, 0, true);

		// jump <= 30
		int F(int i, int jump, bool canGoDown)
		{
			if (i < 0 || i - k > 2) return 0;
			if (memo.TryGetValue((i, jump, canGoDown), out var cached)) { return cached; }

			int self = i == k ? 1 : 0;
			var makeJump = F(i + (1 << jump), jump + 1, true);
			var goDown = canGoDown ? F(i - 1, jump, false) : 0;
			return memo[(i, jump, canGoDown)] = self + makeJump + goDown;
		}
	}
}
