class Solution {
public:
    int choose(int n, int k){
        if(k==0)return 1;
        if(k+k>n) return choose(n, n-k);
        long long answer = 1;
        for(int i = n-k+1;i<=n;i++){
            answer*=i;
            answer/=(i-(n-k));
        }
        return answer;
    }
    int waysToReachStair(int k) {
        int curr=1;
        int answer =0;
        for(int i=0;curr<=i+k+1;i++){
            if(curr>=k) answer+=choose(i+1, curr-k);
            curr+=pow(2, i);

        }
        return answer;
    }
};
