class Solution {
public:
    long long nchoosek(int n, int k){
        if(k == 0)
            return 1;
        return n*nchoosek(n-1, k-1)/k;
    }
    int waysToReachStair(int k) {
        if(k <= 4){
            if(k == 0)
                return 2;
            if(k == 1)
                return 4;
            if(k == 2)
                return 4;
            if(k == 3)
                return 3;
            if(k == 4)
                return 2;
        }
        int x = 1;
        while(1<<x < k)
            x++;
        //cout << x << '\n';
        int y = (1<<x) - k;
        if(y > x+1)
            return 0;
        if(y > (x+1)/2)
            y = x+1-y;
        //cout << x+1 << ' ' << y << '\n';
        return nchoosek(x+1, y);

    }
};
