class Solution:
    def waysToReachStair(self, k: int) -> int:
        @lru_cache(None)
        def dfs(f, now, jump):
            ok = 0
            # print(f, now, jump)
            if now == k: ok += 1
            if now > k + 2: return 0
            # if now > k: 
            #     return now - k
            up = dfs(True, now + (1 << jump), jump + 1)
            down = dfs(False, now - 1, jump) if now >= 1 and f is True else 0
            return ok + up + down
        return dfs(True, 1, 0)
