class Solution {
public:
    int nchoose(int a, int b) {
        b = min(b, a-b);
        long ans1 = 1;

        for (int i=1; i<=b; i++) {
            ans1 *= (a-i+1);
            ans1 /= i;
        }

        long long y = INT_MAX;
        y++;
        int ans = (ans1)%(y);
        return ans;
    }
    int waysToReachStair(int k) {
        if (k == 0) return 2;
        if (k == 1) return 4;

        int diff = k-1;
        int sum = 0;
        int pow2 = 1;
        int count = 1;

        int ans = 0;

        while(sum <= diff) {
            if (sum - diff >= 0 && (sum-diff) <= count) {
                ans += nchoose(count, sum-diff);
            }

            sum += pow2;
            pow2 *= 2;
            count++;

        }

        if (sum - diff >= 0 && (sum-diff) <= count) {
            ans += nchoose(count, sum-diff);
        }

        return ans;
    }
};
