class Solution {
    int c(int n, int k) {
        if (k < 0 || k > n) return 0;
        if (k == 0 || k == n) return 1;
        long long res = 1;
        for (int i = 1; i <= k; i++) {
            res = res * (n - i + 1) / i;
        }
        return res;
    }

public:
    int waysToReachStair(int k) {
        long long p2 = 1, pk = 1, cur = 0;

        int ans = k <= 1;
        while (true) {
            cur += p2;
            int lo = 1 + cur - (pk + 1);
            int hi = 1 + cur;
            if (lo > k) break;
            if (hi >= k) {
                int r = hi - k;
                ans += c(pk + 1, r);
            }
            p2 *= 2;
            pk++;
        }

        return ans;
    }
};
