class Solution:
    def waysToReachStair(self, k: int) -> int:
        @lru_cache(None)
        def comb ( pos,jump,used):
            # print(pos,jump,used)
            ans = 0
            if pos > k and pos-k > 1:
                return 0
            if pos ==k :
                ans += 1
            if pos > 0 and not used:
                ans += comb (pos-1,jump,True)
            ans += comb(pos+2**jump,jump+1,False)
            # print(pos,jump,used,ans)
            return ans
        # print()
        return comb(1,0,False)
