class Solution {
public:
    #define ll __int128
    int ncr(int n, int r){
        if(r > n) return 0 ;
        if(r > n - r) r = n - r ;
        ll ans = 1;
        for(ll i = n ; i > n - r; i --) ans *= i ;
        for(ll i = 1; i <= r; i ++) ans /= i ;
        return (int)ans;
    }

    int waysToReachStair(int k) {
        if(k == 0) return 2;
        if(k == 1) return 4 ;

        int sum = 0 , cnt = 1;
        int ans = 0 ; k -- ;

        for(int i = 1; i <= 2e9; i *= 2){
            sum += i ;
            cnt ++ ;
            if(sum >= k){
                int req = sum - k;
                if(req > cnt) break;
                ans += ncr(cnt, req);
            }
        }

        return ans ;
    }
};
