class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def move(used_go_down, start, jump):
            ans = 0
            if start == k:
                ans += 1
            if start > k + 2:
                return ans
            if used_go_down or start == 0:
                ans += move(False, start + 2**jump, jump + 1)
            else:
                ans += move(False, start + 2**jump, jump + 1)
                ans += move(True, start - 1, jump)
            return ans
        return move(False, 1, 0)
