class Solution:
    def waysToReachStair(self, k: int) -> int:
        @lru_cache(None)
        def solve(i,j,x):
            if i<0:
                return 0
            if i>k+1:
                return 0
            ans=0
            if i==k:
                ans=1
            ans+=solve(i+(1<<j),j+1,0)
            if x==0:
                ans+=solve(i-1,j,1)
            return ans
        return solve(1,0,0)
