class Solution {

    public int waysToReachStair(int k) {
        if(k == 0)
            return 2;
        if(k == 1)
            return 4;
        if(k == 2)
            return 4;
        if(k == 3)
            return 3;
        if(k == 4)
            return 2;
        k--;
        int j = 1;
        int m = 0;
        int base = 1;
        while(k > 0){
            if(k % 2 == 0)
                m += base;
            k /= 2;
            j++;
            base *= 2;
        }
        if(m > j)
            return 0;
        return (int) nCk(j, m);

    }

    long nCk(int n, int k){
        long res = 1;
        for(int i = 0; i < k; i++){
            res = (res * (n - i))/(i + 1) ;
        }
        return res;
    }


}
