class Solution {
public:
    typedef long long ll;
    int waysToReachStair(int k) {
        ll K=k,ans=0;
        static ll C[50][50];
        for(int i=0;i<50;i++) {
            C[i][0] = 1;
            for(int j=1;j<=i;j++) {
                C[i][j] = C[i-1][j] + C[i-1][j-1];
            }
        }
        for(int up=-1;up<=40;++up) {
            ll S=1;
            for(int u=0;u<=up;++u) S+=1LL<<u;
            if(S<K) continue;
            S-=K;
            if(S<=up+2)
            ans+=C[up+2][S];
            bad:;
        }
        return ans;
    }
};
