class Solution {
public:
    unordered_map<int,int> dp[34];
    int waysToReachStair(int k) {
        return helper(1, 0, k);
    }

    int helper(int stair, int jump, int k) {
        int ans = 0;
        if (stair == k || stair == k + 1) ans = 1;
        if (stair > k + 1) return 0;
        if (dp[jump].find(stair) != dp[jump].end()) return dp[jump][stair];
        return dp[jump][stair] = ans + helper(stair + pow(2, jump), jump + 1, k) + helper(stair + pow(2, jump) - 1, jump + 1, k);
    }
};
