class Solution {
public:
    unordered_map<string,int> dp;
    int goal;
    int hp(int pos,int bop,int jump){
        if(pos>goal+1){
            return 0;
        }
        string s=to_string(pos)+"-"+to_string(bop)+"-"+to_string(jump);
        if(dp.find(s)!=dp.end()){
            return dp[s];
        }
        if(pos==0){
            return dp[s]=hp(pos+(1<<jump),1,jump+1)+(pos==goal);
        }
        int ans=hp(pos+(1<<jump),1,jump+1)+(pos==goal);
        if(bop){
            ans+=hp(pos-1,0,jump);
        }
        return dp[s]=ans;
    }
    int waysToReachStair(int k) {
        goal=k;
        return hp(1,1,0);
    }
};
