class Solution {
public:

    unordered_map<int, int> answers[40][2];

    int helper(int j, bool cjb, int s, int &k) {
        if (s > k + 1) return 0;

        if (answers[j][cjb].find(s) != answers[j][cjb].end()) return answers[j][cjb][s];

        return answers[j][cjb][s] = (s == k) + helper(j + 1, true, s + pow(2, j), k) + (cjb ? ((s == 0) ? 0 : helper(j, false, s - 1, k)) : 0);
    }

    int waysToReachStair(int k) {
        return helper(0, true, 1, k);
    }
};
