class Solution:
    def waysToReachStair(self, k: int) -> int:
        @lru_cache(None)
        def dp(stair, jump, last_jump):
            # last_jump == True allows decent one stair
            if stair > k+1:
                return 0
            if stair < 0:
                return 0
            res = 0
            if stair == k:
                res += 1  # Reached the target stair
            if last_jump == True:
                res += dp(stair-1, jump, False)
            res += dp(stair+2**jump, jump+1, True)
            return res
        return dp(1, 0, True)
