# dp
class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dp(i,jump,last):   # last mean last use operation 1 
            res=(i==k)
            if last==0 and i>0:
                res+=dp(i-1, jump, 1)
            if i+2**jump<=k+1:
                res+=dp(i+2**jump, jump+1, 0)                
            return res
        ans=dp(1, 0, 0)
        return ans
