class Solution {
public:
    int ncr(int n,int r){
        long long int ans=1;
        for(int i=0;i<r;i++){
            ans=(ans*1LL*(n-i))/(i+1);
        }
        int h=ans;
        return h;

    }
    int waysToReachStair(int k) {
        long long ans=0;
        for(int i=0;i<=30;i++){
            int num=k-1+i;
            int k=0;
            for(int j=0;j<=30;j++){
                int ch=(1LL<<j);
                bool flag=ch&num;
                if(!flag){
                    k=j;
                    break;
                }
            }
            bool can=true;
            for(int j=k+1;j<=30;j++){
                int ch=(1LL<<j);
                bool flag=ch&num;
                if(flag){
                    can=false;
                    break;
                }
            }
            if(can){
                int cnt=k;
                if(i>cnt+1)continue;
                ans+=ncr(cnt+1,i);
            }

        }
        return ans;
    }
};
