from math import log2, ceil, comb

class Solution:
    def waysToReachStair(self, k: int) -> int:
        if k == 0:
            return 2

        result = 0
        m = ceil(log2(k))
        while True:
            n = 2 ** m - k
            if n < 0 or n > m+1:
                return result

            result += comb(m+1, n)
            m += 1
        return result
