class Solution {
public:
    long long c(long long n, long long r){
        long double sum = 1;
        for(long long i = 1; i <= r; i++){
            sum = sum * (n - r + i) / i;
        }
        return (int)sum;
    }

    int waysToReachStair(int k) {
        long long v=1;
        long long ans=0;
        if(v==k){
            ans++;
        }
        if(v-1==k){
            ans++;
        }
        for(int i=0;i<50;i++){
            v+=(1ll<<i);
            if(v==k)ans++;
            for(long long p=1;p<=i+2;p++){
                if(v-p==k){
                    // cout<<i+2<<" "<<p<<endl;
                    ans+=(c(i+2,p));
                }
            }
        }
        return ans;
    }
};
