class Solution:
    def waysToReachStair(self, target: int) -> int:

        @cache
        def ways(curr, jump=0, consecutive=False):
            ans = 0
            if curr == target:
                ans += 1

            elif curr > target:
                if consecutive:
                    return 0
                elif curr > target + 1:
                    return 0
                #print("here", curr)
                return ways(curr - 1, jump,  True)


            ans += ways(curr + 2 ** jump, jump + 1, False)
            if not consecutive:
                ans += ways(curr - 1, jump, True)
            return ans
        return ways(1)
