class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dfs(stair,last_down,jump):
            if stair>k+1:
                return 0
            ans = 0
            if last_down:
                if stair==k:
                    ans+=1
                ans+=dfs(stair+2**jump,False,jump+1)
            else:
                if stair==k:
                    ans+=1
                ans+=dfs(stair-1,True,jump)
                ans+=dfs(stair+2**jump,False,jump+1)
            return ans

        return dfs(1,False,0)
