class Solution {
public:
    int recur(int n,int k)
    {
        if(n<k)return 0;
        if(n==k || k==0)return 1;
        vector<vector<int>>ans(n+1,vector<int>(k+1,0));
        for(int i=0;i<=n;++i)
        {
            int min_ways=min(i,k);
            // cout<<min_ways;
            for(int j=0;j<=min_ways;++j)
            {
                if(i==j || j==0 || i==0) ans[i][j]=1;
                else ans[i][j]=ans[i-1][j-1]+ans[i-1][j];
            }
        }
        return ans[n][k];
    }
    int waysToReachStair(int k) {
        int ans=0;
        for(int i=0;i<32;++i)
        {
            long long shift=(1LL<<i)-k;
            if(shift<0 || shift>i+1)continue;
            ans+=recur(i+1,shift);
        }
        return ans;
    }
};
