class Solution {
public:
    unordered_map<string, int> mp;
    int util(int i, int jump, int k, bool last) {
    if(i > k+1) return 0;
    // if (i == k) return 1;


    string key = to_string(i) + "_" + to_string(jump) + "_" + to_string(last);
    if (mp.find(key) != mp.end()) return mp[key];

    int ans = 0;
    if (i > 0 && !last) {
        ans += util(i-1, jump, k,true);
    }

    ans += util(i + (1 << jump), jump + 1, k,false);
    ans += (i == k);
    return mp[key] = ans;
}
    int waysToReachStair(int k) {
        return util(1,0,k,false);
    }
};
