class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def calc(m,n):
            if m == 0: return 1
            if m == 1: return n
            if n == 1: return 1 if m == 1 else 0
            if n == 2: return 2*(1 if m == 1 else 0)
            return calc(m,n-1)+calc(m-1,n-2)

        ans = 0
        k -=  1

        for i in range(40):
            if 2**i-1 < k: continue
            d = 2**i-1-k
            if d > i+1: break
            ans += calc(d,i+d)

        return ans
