class Solution {
public:

    int solve(int i,int j,int k,map<vector<int>,int> &mp,int p){
        if(i>k+1){
            return 0;
        }
        if(i==k+1){
            if(p==0)
                return 0;
            if(j==0)
                return 2;
            return 1;
        }
        if(i==k){
            if(j==0){
                if(p==0)
                    return 2;
                return 4;
            }
            if(j==1){
                if(p==0)
                    return 1;
                return 2;
            }
            return 1;
        }
        if(mp.find({i,j,p})!=mp.end()){
            return mp[{i,j,p}];
        }
        if(i==0){
            int x=solve(i+(1<<j),j+1,k,mp,1);
            return mp[{i,j,p}]=x;
        }

        int x=solve(i+(1<<j),j+1,k,mp,1);
        if(p){
            x+=solve(i-1,j,k,mp,0);
        }
        return mp[{i,j,p}]=x;
    }
    int waysToReachStair(int k) {

        map<vector<int>,int> mp;
        int x=solve(1,0,k,mp,1);
        return x;

    }
};
