import math
class Solution:
    def waysToReachStair(self, k: int) -> int:
        jumps = []
        curr_jump = 0
        while True:
            if 2**curr_jump - curr_jump - 1 <= k <= 2 ** curr_jump:
                jumps.append(curr_jump)

            if k < 2**curr_jump - curr_jump - 1:
                break

            curr_jump += 1
        res = 0
        for i in jumps:
            backs = 2**i - k
            res += math.comb(i + 1, backs)
        return res
