class Solution:
    def waysToReachStair(self, k: int) -> int:

        # backward true if i-1
        @cache
        def dp(stair, backward, jump):
            if stair > k + 1 or (stair == k + 1 and not backward):
                return 0

            ways = 1 if stair == k else 0
            if stair > 0 and backward:
                ways += dp(stair - 1, False, jump)
            ways += dp(stair + 2 ** jump, True, jump + 1)
            return ways

        return dp(1, True, 0)
