typedef long long ll;

unsigned long long nCr(int n, int r) {
    if (r > n) {
        return 0;
    }
    if (r == 0 || r == n) {
        return 1;
    }
    if (r > n - r) { // Since C(n, r) == C(n, n-r)
        r = n - r;
    }

    unsigned long long result = 1;
    for (int i = 0; i < r; ++i) {
        result *= (n - i);
        result /= (i + 1);
    }

    return result;
}

class Solution {
public:
    int waysToReachStair(int k) {
        int ans=0;
        // calfac(2e9);
        long long cur=1,power=1,op=0,n=1;
        for(int i=0;i<35;i++){
            long long r=cur-k;
            // cout<<cur<<endl;
            if(r<=n&&r>=0){
                // cout<<n<<" "<<r<<endl;
                ans+=nCr(n,r);
            }
            cur+=power;
            power*=2;
            op++;
            n++;
        }
        return ans;

    }
};
