vector<vector<int>> combTable(31, vector<int>(31,-1));
class Solution {
public:
    int myComb(int a, int b){
        if(combTable[a][b]!=-1){
            return combTable[a][b];
        }

        if(b==0){
            return combTable[a][b]=1;
        }
        if(b==1){
            return combTable[a][b]=a;
        }
        if(a==b){
            return combTable[a][b]=1;
        }
        if(a==(b-1)){
            return combTable[a][b]=a;
        }
        return combTable[a][b]=myComb(a-1,b)+myComb(a-1,b-1);
    }
    int waysToReachStair(int k) {
        // return myComb(4,2);
        int ans = 0;
        int stair = 1;
        int downs = 0;
        int jumps = 0;
        while(k>=(stair-jumps-1)){
            if(stair>=k){
                downs = stair-k;
                ans += myComb(jumps+1,downs);
            }
            stair *= 2;
            jumps += 1;
        }
        return ans;

    }
};
