class Solution:
    def waysToReachStair(self, k: int) -> int:
        # jump = 0
        # pos = 1
        # once to i-1
        # go to stair i + 1<<jump ; jump++
        mem = {}
        def search(curr: int, canRetract: bool, jump: int) -> int:
            if curr > k+1 or (curr == k+1 and not canRetract):
                return 0

            t = (curr, canRetract, jump)
            if t in mem:
                return mem[t]
            res = 1 if curr == k else 0
            res += search(curr + (1<<jump), True, jump+1)
            if canRetract:
                res += search(curr-1, False, jump)
            mem[t] = res
            return res

        return search(1, True, 0)
