int dx[] = {0, 0, -1, 1, -1, -1, 1, 1};
int dy[] = {-1, 1, 0, 0, -1, 1, -1, 1};

int gcd(int a, int b) { return b ? gcd(b, a % b) : a; }

class Solution {
public:
    vi f(int x) {
        vi res{1};
        while(x--) {
            res.pb(1);
            for(int i = res.size() - 2; i > 0; --i) {
                res[i] += res[i-1];
            }
        }
        return res;
    }
    int waysToReachStair(int k) {
        return solve(k) + solve(k+1);
    }
    int solve(int x) {
        if(x == 0) return 0;
        if(x <= 2) return 2;
        int r = 0;
        int xx = x - 1;
        while(xx) {
            ++r;
            xx >>= 1;
        }
        int idx = (1 << r) - x;
        if(idx > r) return 0;
        int res = f(r)[idx];
        return res;
    }
};
