class Solution:
    def waysToReachStair(self, k: int) -> int:

      @cache
      def go(i: int, jump: int, can_back: bool) -> int:
        if i >= k + 2:
          return 0

        res = 0
        if i == k:
          res += 1

        res += go(i+2**jump, jump+1, True)
        if can_back:
          res += go(i-1, jump, False)
        return res

      return go(1, 0, True)
