class Solution {
public:
    map<vector<int>,int> dp;
    int f(int i,int k,int j,bool fl)
    {
        if(i>k+1)
            return 0;
        if(dp.find({i,j,fl})!=dp.end())
            return dp[{i,j,fl}];
        if(i==k)
        {
            int ans=1;
            if(fl)
                ans+=f(i-1,k,j,!fl);
            if(!fl)
            ans+=f(i+(1ll<<j),k,j+1,!fl);
            else
                ans+=f(i+(1ll<<j),k,j+1,fl);
            return dp[{i,j,fl}]=ans;
        }
        int ans=0;
        if(fl)
                ans+=f(i-1,k,j,!fl);
        if(fl)
            ans+=f(i+(1ll<<j),k,j+1,fl);
        else
            ans+=f(i+(1ll<<j),k,j+1,!fl);
        return dp[{i,j,fl}]=ans;
    }
    int waysToReachStair(int k) {
        int ans=f(1,k,0,true);
        return ans;
    }
};
