class Solution:
    def waysToReachStair(self, k: int) -> int:
        ans = 0
        pos = 1
        jump = 0
        l = 1

        while True:
            if pos == k:
                ans += 1
            elif pos > k:
                d = pos - k
                if d > jump + 1:
                    break
                r = 1
                for i in range(2, jump + 2):
                    r *= i
                for i in range(2, d + 1):
                    r //= i
                for i in range(2, jump + 2 - d):
                    r //= i
                ans += r

            pos += l
            jump += 1
            l *= 2

        return ans
