record State(int i, int jump, boolean justWentBack){}

class Solution {
    Map<State, Integer> dp = new HashMap<>();
    public int waysToReachStair(int k) {
        return waysToReachStairHelper(1, 0, false, k);
    }

    int waysToReachStairHelper(int i, int jump, boolean justWentBack, int target) {

        State state = new State(i, jump, justWentBack);
        if (dp.containsKey(state)) {
            return dp.get(state);
        }
        if (i < 0 || i > target + 2) {
            return 0;
        }
        int reVal = 0;
        if (target == i) {
            reVal++;
        }
        if (!justWentBack) {
            reVal += waysToReachStairHelper(i - 1, jump, true, target);
        }

        reVal += waysToReachStairHelper(i + (int)Math.pow(2, jump), jump + 1, false,target);

        dp.put(state, reVal);
        return reVal;
    }
}
