#define ll long long

ll POWER(ll a , ll x){
    ll ans = 1 ; 
    while(x){
        if(x&1 != 0){ans = ((ans)*(a));}
        a =  ((a)*(a)) ;x = x>>1;
    }return ans ;
}

map<pair<ll , ll> , ll> dp ; 

class Solution {
    ll sol(ll i , int &k , ll jump){
        if(i >= k+2) return 0 ;

        if(dp.find({i , jump}) != dp.end()) return dp[{i , jump}] ; 

        ll a = sol(i + POWER(2 , jump) , k , jump+1) ; 
        ll b = 0 ; 
        if(i != 0) b = sol(i - 1 + POWER(2 , jump) , k , jump+1) ; 
        if(i == k) a++; 
        if(i - 1 == k) b++; 
        return dp[{i , jump}] = a + b ; 
    }
public:
    int waysToReachStair(int k) {
        dp.clear(); 
        return sol(1 , k , 0) ; 
    }
};
