class Solution {
public:
    int waysToReachStair(int k) {
        long long cnt = 1;
        long long lom = 0;
        long long ans = 0;
        long long sum = 1;
        while(true) {
            long long maxi = sum + lom;

            if (maxi >= k && maxi-cnt <= k) {
                int take = maxi-k;
                ans += combin(cnt, take);
            }
            if (maxi-cnt > k) break;

            sum += lom;
            lom *= 2;
            if (lom == 0) lom++;

            cnt++;
        }
        return ans;
    }

    long long combin(int n, int r) {
        long long temp = 1;
        for(int i=0; i < r; i++) {
            temp *= (n-i);
            temp /= (i+1);
        }

        return temp;
    }

    long long fact(int n) {
        if (n == 0) return 1;
        return fact(n-1)*n;
    }
};
